INSERT INTO `qiq_information` VALUES('25','惊呆了','惊呆了乐乐乐乐乐','&lt;p style=&quot;text-align: center;&quot;&gt;2017-03-2712:26:09&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;img src=&quot;/ueditor/php/upload/image/20170327/1490589667694102.jpg&quot; title=&quot;1490589667694102.jpg&quot; alt=&quot;u=2607505119,2641349637&amp;amp;fm=206&amp;amp;gp=0.jpg&quot;/&gt;&lt;/p&gt;&lt;h1 style=&quot;border-bottom-color:#cccccc;border-bottom-width:2px;border-bottom-style:solid;padding:0px 4px 0px 0px;margin:0px 0px 10px;&quot;&gt;&lt;span style=&quot;color:#e36c09;&quot; class=&quot;ue_t&quot;&gt;[此处键入简历标题]&lt;/span&gt;&lt;/h1&gt;&lt;p&gt;&lt;span style=&quot;color:#e36c09;&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;table width=&quot;100%&quot; border=&quot;1&quot; style=&quot;border-collapse:collapse;&quot;&gt;&lt;tbody&gt;&lt;tr class=&quot;firstRow&quot;&gt;&lt;td width=&quot;200&quot; style=&quot;text-align:center;&quot; class=&quot;ue_t&quot;&gt;【此处插入照片】&lt;/td&gt;&lt;td&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;联系电话：&lt;span class=&quot;ue_t&quot;&gt;[键入您的电话]&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;电子邮件：&lt;span class=&quot;ue_t&quot;&gt;[键入您的电子邮件地址]&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;家庭住址：&lt;span class=&quot;ue_t&quot;&gt;[键入您的地址]&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/tbody&gt;&lt;/table&gt;&lt;h3&gt;&lt;span style=&quot;color:#e36c09;font-size:20px;&quot;&gt;目标职位&lt;/span&gt;&lt;/h3&gt;&lt;p style=&quot;text-indent:2em;&quot; class=&quot;ue_t&quot;&gt;[此处键入您的期望职位]&lt;/p&gt;&lt;h3&gt;&lt;span style=&quot;color:#e36c09;font-size:20px;&quot;&gt;学历&lt;/span&gt;&lt;/h3&gt;&lt;p&gt;&lt;span style=&quot;display:none;line-height:0px;&quot;&gt;﻿&lt;/span&gt;&lt;/p&gt;&lt;ol style=&quot;list-style-type: decimal;&quot; class=&quot; list-paddingleft-2&quot;&gt;&lt;li&gt;&lt;p&gt;&lt;span class=&quot;ue_t&quot;&gt;[键入起止时间]&lt;/span&gt; &lt;span class=&quot;ue_t&quot;&gt;[键入学校名称] &lt;/s