INSERT INTO `qiq_information` VALUES('26','震惊，吓呆了','震惊啊，震惊啊。好可怕','&lt;p&gt;第三方郭德纲梵蒂冈豆腐干是是非非敢死队&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;img src=&quot;/ueditor/php/upload/image/20170408/1491638586116383.jpg&quot; title=&quot;1491638586116383.jpg&quot;/&gt;&lt;/p&gt;&lt;p&gt;&lt;img src=&quot;/ueditor/php/upload/image/20170408/1491638596115415.jpg&quot; title=&quot;1491638596115415.jpg&quot;/&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;','17-04-08 04:03:27','','震惊王','4','来自自编','1','震惊|怕怕|','2','2017-04-08/20170408160327.jpg');
