--
-- 表的结构qiq_plan
--

DROP TABLE IF EXISTS `qiq_plan`;
CREATE TABLE `qiq_plan` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '主键',
  `Uid` int(11) NOT NULL COMMENT '用户ID',
  `Tid` int(11) NOT NULL COMMENT '团队id',
  `plan_type` varchar(20) COLLATE utf8mb4_unicode_ci NOT NULL COMMENT '策划书类型',
  `plan_route` varchar(50) COLLATE utf8mb4_unicode_ci NOT NULL COMMENT '策划书存放路径',
  `plan_title` varchar(50) COLLATE utf8mb4_unicode_ci NOT NULL COMMENT '策划书标题',
  `planup_time` varchar(15) COLLATE utf8mb4_unicode_ci NOT NULL COMMENT '策划书上传时间',
  `plan_stage` varchar(20) COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT '策划书' COMMENT '状态阶段',
  `plan_summary` varchar(300) COLLATE utf8mb4_unicode_ci NOT NULL COMMENT '简介',
  `plan_cover` varchar(50) COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT 'plan-default.jpg' COMMENT '策划书封面',
  `plan_state` int(11) NOT NULL DEFAULT '0' COMMENT '审核状态',
  `plan_times` int(11) NOT NULL DEFAULT '0' COMMENT '浏览次数',
  `plan_highlights` text COLLATE utf8mb4_unicode_ci,
  `plan_rule` text COLLATE utf8mb4_unicode_ci NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=27 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

