DROP TABLE IF EXISTS `qiq_user`;
CREATE TABLE `qiq_user` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `username` varchar(50) COLLATE utf8mb4_unicode_ci NOT NULL,
  `password` varchar(64) COLLATE utf8mb4_unicode_ci NOT NULL,
  `sex` varchar(2) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `realname` varchar(20) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `phone` varchar(11) COLLATE utf8mb4_unicode_ci NOT NULL,
  `email` varchar(50) COLLATE utf8mb4_unicode_ci NOT NULL,
  `occupation` varchar(50) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `motto` varchar(150) COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT '个性签名',
  `user_score` int(11) NOT NULL DEFAULT '0',
  `user_grade` int(11) NOT NULL DEFAULT '1',
  `province` varchar(20) COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT '省级地址',
  `city` varchar(20) COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT '市级地址',
  `district` varchar(20) COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT '县级地址',
  `head_pic` varchar(64) COLLATE utf8mb4_unicode_ci NOT NULL,
  `reg_time` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
  `reg_ip` varchar(64) COLLATE utf8mb4_unicode_ci NOT NULL,
  `user_qq` varchar(20) COLLATE utf8mb4_unicode_ci NOT NULL,
  `user_wechat` varchar(50) COLLATE utf8mb4_unicode_ci NOT NULL,
  `user_weibo` varchar(50) COLLATE utf8mb4_unicode_ci NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=63 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

