DROP TABLE IF EXISTS `qiq_video`;
CREATE TABLE `qiq_video` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '主键',
  `Uid` int(11) NOT NULL COMMENT '用户id',
  `Tid` int(11) NOT NULL COMMENT '团队id',
  `video_title` varchar(50) COLLATE utf8mb4_unicode_ci NOT NULL COMMENT '视频标题',
  `video_summary` varchar(300) COLLATE utf8mb4_unicode_ci NOT NULL COMMENT '视频简介',
  `video_cover` varchar(50) COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT 'video-default.jpg' COMMENT '视频封面（路径）',
  `video_route` varchar(50) COLLATE utf8mb4_unicode_ci NOT NULL COMMENT '视频（存储路径）',
  `user_name` varchar(20) COLLATE utf8mb4_unicode_ci NOT NULL COMMENT '用户名',
  `vidup_time` varchar(30) COLLATE utf8mb4_unicode_ci NOT NULL COMMENT '视频上传时间',
  `play_times` int(11) NOT NULL DEFAULT '0' COMMENT '视频播放次数',
  `video_mark` int(11) NOT NULL DEFAULT '0' COMMENT '视频评分',
  `video_praise` int(11) NOT NULL DEFAULT '0' COMMENT '点赞个数',
  `video_collection` int(11) NOT NULL DEFAULT '0' COMMENT '收藏个数',
  `video_type` varchar(20) COLLATE utf8mb4_unicode_ci NOT NULL COMMENT '视频类型',
  `video_state` int(11) NOT NULL DEFAULT '0' COMMENT '视频审核状态',
  `is_change` int(3) NOT NULL DEFAULT '0',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=28 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

