  `videoid` int(11) NOT NULL,
  `channels` int(11) DEFAULT NULL,
  `abit_rate` int(11) DEFAULT NULL,
  `width` int(11) DEFAULT NULL,
  `height` int(11) DEFAULT NULL,
  `vbit_rate` int(11) DEFAULT NULL,
  `duration` varchar(40) DEFAULT NULL,
  `size` varchar(20) DEFAULT NULL,
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `acodec_name` varchar(30) DEFAULT NULL,
  `vcodec_name` varchar(30) DEFAULT NULL,
  `avg_frame_rate` varchar(20) DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=47 DEFAULT CHARSET=utf8;

