INSERT INTO `qiq_team` (`id`, `Uid`, `team_name`, `team_slogan`, `team_summary`, `team_pic`, `team_times`, `status`) VALUES
(1, 37, '智慧队', '智慧创艺，汇聚创业', '这是一个互帮互助的团队，有着明确的分工和开阔的眼界， 他们不受传统的拘泥，敢想敢做，有着创业的激情和恒心，一直保持着严谨求实的态度， 相信运气就是机会碰巧撞到了努力，踏实地付出努力总能换来成功，Just do it！', '5911a255eeac0.png', 4, 1),
(2, 51, '创意团队', '创意、创意、还是创意', '我们以敏锐的目光，鲜明的个性，寻求心灵与心灵的沟通。我们以至诚的态度，为您服务！我们是以商务礼仪庆典、活动广告策划为主的综合文化传播公司，旗下拥有专业艺术院校的演员组成的专业表演团队,拥有强大的演出阵容及丰富的文艺演出节目。', '590f7747335b9.png', 0, 1),
(5, 35, '开黑打代码', '给力，给力，勇往直前！给力，给力，冲破无限！', '成都影上文化传媒有限公司在成都设立公司，是在中国影视传媒事业蓬勃发展的大环境下，快速成长起来的覆盖整体影视产业链的股份制影视传媒公司。影上致力于从核心商业电影产品入手，结合市场研发，发掘和运作优质电影项目，联合超一流的制作团队，配合各种有效资源，宣发先行，整合影视产业的全产业链资源。我们运用移动互联网时代的创新技术和传播手段，整合文化跨领域资源，拓展广阔的文化衍生市场。', '590f738ee37be.png', 0, 1),
(6, 64, '综艺产业团队', '没有最难的事情，只有最新的创意', '这是一支新兴的综艺创意创业团队，我们会不定时提供各种综艺创意', '590da24fe7c0e.png', 0, 1),
(7, 39, '精英队', '精领天下，英才神话', '凝聚一种力量，提供一种精神，共同完成一个使命。卓越非凡的团队，才华横溢的精英，独具创意的领袖，不同寻常的经历，梦想与现实的交融，一演一次又一次震撼人心的传奇', '59115762a0f70.png', 8, 1),
(8, 67, '333333', '333333', '333333', '5911bf9f4189a.png', 0, 0);
INSERT INTO `qiq_teammember` (`id`, `Tid`, `member_name`, `member_summary`, `member_type`) VALUES
(35, 2, '杨金林', '毕业于浙江传媒学院多媒体制作专业，浙江传媒学院“浙江省非物质文化遗产研究基地”特聘教师。长期从事电影、纪录片、电视节目创作，以及负责部门产学研相关工作。', '导师'),
(22, 3, '陈来了', '哈哈哈哈', '导师'),
(23, 3, '明明名', '2007年获得华南理工大学博士学位，现任东莞理工学院计算机学院副院长，广东省电子商务大数据工程技术中心主任，是广东工业大学等国内五校硕士导师、广东省科技咨询专家、广东省自然科学基金同行评议专家、广东省软件与信息服务业评审专家、广东省综合评标专家和东莞市科技咨询专家。 秦教授主要致力于计算机网络中负载平衡理论与实现技术。光互连OTIS网络负载平衡理论、算法和调度。QosR优化中的MCP等问题的研究。主持完成了广东省自然科学基金项目1项、广东省教育厅自然科学研究项目1项、参与1项国家863项目的研究工作。主持或主要承担完成了东莞、佛山、惠州、珠海等市科技计划重点项目和产学研项目等多项纵向横向课题3', '成员'),
(26, 4, '广泛大概', '鬼地方', '导师'),
(25, 4, '防守对方', '范德萨', '导师'),
(32, 5, '郭虎', '郭虎师从香港著名导演林玉芬。 曾任大型剧组执行导演，副导演。代表作品《花千骨》执行导演。《三生三世十里桃花》执行导演。《步步惊心》执行导演。《无心法师》执行导演。《微微一笑很倾城》执行导演。《湄公河大案》执行导演。 院线电影：2016年导演院线《醉+拍档》', '导师'),
(30, 6, '李天胜', '就读于XX大学电子信息工程专业，在校期间热爱所学专业，认真学习专业基础知识，注重实验实践锻炼。曾获得国家励志奖学金一次、校优秀学生校友奖学金一次、校优秀学生奖学金二等奖两次;两次获得校“优秀团干”，两次获校“三好学生”称号;已获得英语四、六级证书和计算机二级C语言合格证书;思想上积极进步，于20XX年获得“优秀共产党员”称号;平时积极参加院系组织的活动，参加学校组织的暑期调研征文比赛并获得一等奖，参加全国大学生英语竞赛学校初赛并取得良好成绩，积极参加院系组织的篮球比赛、演讲比赛等文体活动。大学生活使我收获颇丰，增长了知识，开阔了眼界，综合素质也得到了很大提高。', '导师'),
(31, 6, '陈小脸', '就读与xxx大学，热爱综艺产业。', '成员'),
(33, 5, '付利娟', '成都影上文化传媒有限公司法人，代表作网大电影《生人勿近》《我的七岁男友》《这儿是我家》。拥有非常丰富的制片经验。', '成员'),
(34, 5, '杜安琪', '毕业于北京电影学院，2016主演院线电影《我的青春不怕疼》曾出演过影视剧电影《月光姐妹》、《青铜爱情》、《后会有期》、被观众熟知。演技精湛，有着高于一般演员的颜值辨别度。', '成员'),
(36, 2, '王昌慧', '毕业于宁波大学网络与信息管理专业\r\n浙江真橙影视文化发展有限公司 执行董事兼总经理\r\n韩国天顶集团 中方合伙人\r\n韩国ZENITH GLOBAL娱乐公司 中方合伙人\r\n韩国知名娱乐媒体TOPSTAR NEWS 中方媒体运营合伙人\r\n宁波微电影协会 副会长\r\n宁波东北商会 理事', '成员'),
(37, 7, '精英', '1、培养团队文化，确立团队目标，领导团队行动。 \r\n2、不断吸引最优秀的人才。 \r\n3、拓展顾客，拓展团队，实现宏伟目标。 \r\n4、重现团队协作。 \r\n5、培育内部人才市场，为创立精英团队提供有利条件。 \r\n6、在团队外面寻找新创意，不断提供给团队。 \r\n7、协调空间、程序和时间，鼓励团队成员贡献创意。', '导师'),
(38, 7, '陈员', '心理分析的核心就是“买传闻，卖事实”。孙子兵法云，兵者，诡道也。故能而示之不能，用而示之不用，近而示之远，远而示之近。此乃虚就是实，实就是虚，市场热炒的永远都是预期，当预期真正成为事实后，反倒会被市场资金所冷落。所以，金融市场本身就是逆人性的。', '成员');
