INSERT INTO `qiq_video` (`id`, `Uid`, `Tid`, `video_title`, `video_summary`, `video_cover`, `video_route`, `vidup_time`, `play_times`, `video_mark`, `video_praise`, `video_collection`, `video_type`, `video_state`, `is_change`) VALUES
(1, 35, 5, '慢慢习惯-刘德华', '刘德华致所有警察的一首“情歌”。作为五一档唯一一部重量级城市反恐题材警匪大片，此次发布的主题曲MV《慢慢习惯》则一转风格，刘德华的演唱深沉抒情、画面中的爱情片段细腻感人。MV画面中展现了拆弹专家刘德华与宋佳从相识相知到相爱的点滴，两人生活中的甜蜜与拆弹专家这一职业的危险形成强烈对比。', '2017-05-09/20170509100854.png', '/upload/Video/2017-05-08/59102909aa0b6.mp4', '1494231305', 53, 0, 1, 1, '5', 1, 2),
(2, 35, 0, '戚薇 - 有个人', '戚薇为自己主演的都市情感大戏《繁星四月》演唱《有个人》，作为《繁星四月》的主题曲。戚薇全程参与作词，她对角色的深刻领悟让这首歌的歌词更加贴合剧中人物内心，寥寥数行，却也成为了无数都市人类“双面灵魂”的日常写实。', '2017-05-09/20170509101352.png', '/upload/Video/2017-05-08/59102c35d9ad9.mp4', '1494231344', 41, 0, 0, 0, '4', 1, 2),
(3, 35, 0, '李健 - 春风十里不如你', '“音乐诗人”李健受“文艺诗人”冯唐力邀，亲自操刀为大型青春纯爱电视剧《春风十里，不如你》创作同名主题曲。两大文艺男神的远方终于交集在一起，为该剧的高品质再添重磅。', '59104cd266a12.jpg', '/upload/Video/2017-05-08/59104cd266a12.mp4', '1494240466', 4, 0, 0, 0, '4', 1, 2),
(4, 37, 0, '可惜没有如果', '林俊杰和张怀秋是很好的朋友，一天俩人喝多了回到了母校怀念，林俊杰在母校的剧场弹奏钢琴被音乐老师Angelababy听到了，林俊杰没穿鞋还比较腼腆于是就藏到了台下，Angelababy没有看到林俊杰就把张怀秋误认为是林俊杰了，之后和张怀秋在一起了，其实林俊杰喜欢Angelababy，但是他和张怀秋是好兄弟所以只能心里想着却不不能表明。', '5910519da7d47.jpg', '/upload/Video/2017-05-08/5910519da7d47.mp4', '1494241695', 2, 0, 0, 0, '1', 1, 2),
(5, 61, 1, '五月天 - 突然好想你', '《突然好想你》音乐MV请来陈宏一导演掌镜，歌曲讲述每个人心中甜甜又酸涩的往日恋爱回忆，五月天也特别分享自己的初恋故事', '2017-05-09/20170509143552.png', '/upload/Video/2017-05-08/59106a225073f.mp4', '1494231325', 37, 0, 0, 0, '6', 1, 2),
(6, 37, 0, '薛之谦 - 独家专访薛之谦', '从只会傻傻付出的懵懂“初学者”，到历尽沧桑看破套路的“演员”，薛之谦在向世人展示了自己不同的心路情感。歌手，段子手，火锅店老板，用生命在多元跨界的薛之谦，怎么看新歌的市场反馈？又为何痛恨黄牛党？网易云音乐专访薛之谦。', '2017-05-09/20170509095704.png', '/upload/Video/2017-05-08/59106aab496a5.mp4', '1494231308', 46, 0, 2, 1, '5', 1, 2),
(7, 37, 1, '张芸京 - 偏爱', '《偏爱》是《仙剑奇侠传3》原声带中收录的8个插曲之一，由金牌大风音乐集团出品，2009年张芸京刚拿下超级偶像冠军，应胡歌的邀请，为该剧原声带献唱。', '59106d714866c.jpg', '/upload/Video/2017-05-08/59106d714866c.mp4', '1494231350', 0, 0, 0, 0, '1', 1, 2),
(8, 57, 0, '陈奕迅 - 陪你度过漫长岁月', '由周迅监制，根据安东尼同名畅销书改编的电影《陪安东尼度过漫长岁月》将于11月13日上映，周迅力邀香港乐坛实力唱将陈奕迅开嗓献唱，Eason极具辨识度的嗓音配上悠扬柔美的词曲，狂掀暖心狂潮。主题曲延续Eason一贯的“E式情歌”风格，透过感人的旋律传达心声。', '59106f75b393c.jpg', '/upload/Video/2017-05-08/59106f75b393c.mp4', '1494249334', 1, 0, 0, 0, '3', 1, 2),
(9, 57, 0, '超能陆战队', '《Immortals》是电影《超能陆战队》的主题曲。最初，迪斯尼要求Fall Out Boy乐队为讲述一群学生由高智商变为高科技英雄团队的电影《超能陆战队》谱写和表演一首歌曲。贝斯手Pete Wentz回忆，乐队成员去见迪斯尼董事后，董事向乐队介绍影片故事，电影的主人公们与乐队看世界的方式疯狂地相似，就深藏在Fall Out Boy的DNA中', '5910709235148.jpg', '/upload/Video/2017-05-08/5910709235148.mp4', '1494249618', 6, 0, 0, 0, '2', 1, 2),
(11, 57, 0, '杨宗纬 - 一次就好', '《夏洛特烦恼》暖水曲《一次就好》，为报答观众“自来水”，特别力邀新世代歌神杨宗纬倾情献唱，在电影中是沈腾饰演的夏洛写给喜欢的人的歌，也是夏洛唯一“原创”的歌曲，虽然不曾唱给马丽饰演的马冬梅听，但却成为她最爱的歌曲。', '591072ee01a35.jpg', '/upload/Video/2017-05-08/591072ee01a35.mp4', '1494250222', 8, 0, 0, 1, '2', 1, 2),
(12, 39, 0, '周深 - 大鱼', '7月8日首映的电影《大鱼海棠》推出印象曲MV《大鱼》，MV以电影画面剪辑而成，有展现“其他人”世界绚丽多彩的广阔镜头，又有对椿、鲲、湫情感的细腻刻画，词曲、演唱与MV画面圆融如意，极富中国传统之美。', '5910748fc1d74.jpg', '/upload/Video/2017-05-08/5910748fc1d74.mp4', '1494250640', 19, 0, 2, 2, '1', 1, 2),
(13, 39, 0, '微微一笑很倾城', '杨洋献唱电视剧《微微一笑很倾城》片尾曲\r\n简介：杨洋为《微微一笑很倾城》首度献声电视剧同名OST！《微微一笑很倾城》展露了杨洋作为“最佳男友”肖奈的浪漫一面，甜甜的清澈的少年的教科书般的初恋专属男友。如果，我知道有一天我会这么爱你，我一定对你一见钟情……打造玄幻暑期档“夹击”中的“纯美校园清流OST”。', '2017-05-09/20170509102412.png', '/upload/Video/2017-05-08/591074ff81cbd.mp4', '1494231310', 67, 0, 1, 1, '4', 1, 2),
(20, 66, 0, '邓紫棋专访', '2017年，邓紫棋借用全新巡演主题“Queen of Hearts”,宣告昔日的金鱼妹已经蜕变为“红心女王”。爆红之后的三年她有什么成长？面对非议她是怎样的态度？怎么看待现在的音乐市场？\r\n《超级面对面》独家专访邓紫棋5月4日早9点网易云音乐全平台上线。', '5913edaae1cfd.jpg', '/upload/Video/2017-05-11/5913edaae1cfd.mp4', '1494478252', 0, 0, 0, 0, '5', 1, 2),
(19, 66, 0, '孙燕姿 - 半句再见', '青春电影《六弄咖啡馆》今日发布由歌坛天后孙燕姿 献唱的主题曲《半句再见》MV ，片方特别邀请到歌坛天后孙燕姿跨刀献唱，更是因为作为代表一代人青春回忆的“标签式”人物，孙燕姿和电影《六弄咖啡馆》的独特气质不谋而合。', '591276ad45ea9.jpg', '/upload/Video/2017-05-10/591276ad45ea9.mp4', '1494382254', 1, 0, 0, 0, '6', 0, 2);
